import { Component, EventEmitter, Input, Output } from '@angular/core';
import { disabledBtn, goldHoveringBtn } from 'src/app/constants/buttons';
import { EmptyPagination, Pagination } from 'src/app/model/pagination';

@Component({
  selector: 'custom-paginator',
  templateUrl: './custom-paginator.component.html',
  styleUrls: ['./custom-paginator.component.scss']
})
export class CustomPaginatorComponent {
  @Input() pagination: Pagination<any> = new EmptyPagination();
  @Output() changePageEmitter: EventEmitter<number> = new EventEmitter<number>();
  goldHoveringBtn = goldHoveringBtn;
  disabledBtn = disabledBtn;
  pageArray: number[] = [];

  isStart(): boolean {
    return this.pagination.currentPage === 0;
  }

  canGoBack(): boolean {
    return this.pagination.currentPage > 0;
  }

  canGoAhead(): boolean {
    return (this.pagination.currentPage + 1) < this.pagination.totalPages;
  }

  isEnd(): boolean {
    return (this.pagination.currentPage + 1) === this.pagination.totalPages;
  }

  isActivePage(pageNumber: number | string): boolean {
    if(typeof pageNumber === 'string'){
      return false
    }

    return (this.pagination.currentPage + 1) === pageNumber;
  }

  changePage(pageNumber: number | string) {
    if(typeof pageNumber === 'string'){
      return
    }

    const NEW_PAGE = pageNumber - 1;
    this.changePageEmitter.emit(NEW_PAGE);
  }

  getPageArray(): (number | string)[] {
    let pageArray: (number | string)[] = ['...'];
    const TOTAL_PAGES: number = this.pagination.totalPages;
    const ACTUAL_PAGE: number = this.pagination.currentPage + 1;
    const MAX_VISIBLE_PAGES: number = 5;
    // //if TOTAL_PAGES < 5
    // //if TOTAL_PAGES > 5  --- !(TOTAL_PAGES -3 > actualPage > TOTAL_PAGES)
    // //if TOTAL_PAGES > 5  --- TOTAL_PAGES -3 > actualPage > TOTAL_PAGES

    // if (TOTAL_PAGES <= 5) {
    //   pageArray = Array.from({length: TOTAL_PAGES}, (_, index) => index +1);
    // } else {
    //   const last4minLimit = TOTAL_PAGES - 3;

    //   if (ACTUAL_PAGE >= last4minLimit && ACTUAL_PAGE <= (TOTAL_PAGES)) {
    //     //todo... example: pageArray = ['...', 7, 8, 9, 10]
    //   } else {
    //     //todo... example: pageArray = [1, 2, '...', 9, 10] 
    //   }
    // }

    if (TOTAL_PAGES <= MAX_VISIBLE_PAGES) {
      // Se ci sono 5 pagine o meno, mostro tutte le pagine senza i puntini di sospensione
      return Array.from({ length: TOTAL_PAGES }, (_, index) => index);
    } else {
      const pageArray: (number | string)[] = [];

      if (ACTUAL_PAGE <= 2) {
        // Mostra le prime 5 pagine
        pageArray.push(...Array.from({ length: MAX_VISIBLE_PAGES }, (_, index) => index + 1), '...');
      } else if (ACTUAL_PAGE >= TOTAL_PAGES - 1) {
        // Mostra le ultime 5 pagine
        pageArray.push('...', ...Array.from({ length: MAX_VISIBLE_PAGES - 1 }, (_, index) => TOTAL_PAGES - MAX_VISIBLE_PAGES + index + 1));
      } else {
        // Mostra 5 pagine centrando attorno alla pagina attuale
        pageArray.push('...', ACTUAL_PAGE - 1, ACTUAL_PAGE, ACTUAL_PAGE + 1, '...');
      }

      return pageArray;
    }
  }

  isNaN(value: any): boolean{
    return isNaN(value)
  }
}
