import { Component } from '@angular/core';
import { goldHoveringBtn } from 'src/app/constants/buttons';

@Component({
  selector: 'search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent {
  goldHoveringBtn = goldHoveringBtn;

}
<button [class]="'text-amber-950 h-[30px]'+ goldHoveringBtn">
    <i class="fa-solid fa-magnifying-glass"></i> Search a recipe!
</button>

<section class="absolute top-0 left-0 h-full w-full bg-stone-700 bg-opacity-5"></section>
<dialog open class="post-it absolute w-[18rem] bg-amber-200">

</dialog>
