import { Component } from '@angular/core';
import { goldHoveringBtn } from 'src/app/constants/buttons';

@Component({
  selector: 'search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent {
  goldHoveringBtn = goldHoveringBtn;
  actived: boolean = false

  isActive(): boolean{
    return this.actived
  }
}
<button [class]="'text-amber-950 h-[30px]'+ goldHoveringBtn" [class.text-amber-400]="isActive()" (click)="actived = true">
    <i class="fa-solid fa-magnifying-glass"></i> Search a recipe!
</button>

<section *ngIf="isActive()" class="absolute top-0 left-0 h-full w-full bg-stone-700 bg-opacity-5" (click)="actived = false"></section>
<dialog [open]="isActive()" class="post-it absolute w-[18rem] bg-amber-200">

</dialog>
