import { Injectable } from '@angular/core';
import { Observable, of, throwError } from 'rxjs';
import { Recipe } from '../model/recipe.model';
import { recipesMock } from 'src/assets/mocks/recipe';
import { Pagination } from '../model/pagination';

@Injectable({
  providedIn: 'root'
})
export class RecipeService {

  constructor() { }

  getRecipes(page: number = 0): Observable<Pagination<Recipe>> {
    const ITEMS_PER_PAGE: number = 5;
    const START_ELEM: number = page * ITEMS_PER_PAGE;         // 0*5 = 0      or    1*5 = 5
    const END_ELEM: number = START_ELEM + ITEMS_PER_PAGE;     // 0+5 = 5      or    5+5 = 10

    const RESPONSE: Pagination<Recipe> = {
      content: recipesMock.slice(START_ELEM, END_ELEM || recipesMock.length),
      currentPage: page,
      totalItems: recipesMock.length,
      totalPages: Math.floor(recipesMock.length / ITEMS_PER_PAGE),
      itemPerPage: ITEMS_PER_PAGE
    }
    return of(RESPONSE)
  }

  getRecipe(id: string): Observable<Recipe> {
    const RECIPE = recipesMock.find(recipe => recipe.id === id);

    if (RECIPE) {
      return of(RECIPE);
    } else {
      const errorMessage = `Recipe with id ${id} not found.`;
      return throwError(new Error(errorMessage));
    }
  }
}
